import * as React from 'react';

import './sidebar.scss';
import { Menu, Icon, Image } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';

export interface IProps {
  isSidebarVisible: boolean;
  toggleSidebar: () => void;
}

class Sidebar extends React.Component<IProps> {

  render() {
    const { toggleSidebar, isSidebarVisible } = this.props;
    const sidebarDisplay = isSidebarVisible ? 'block' : 'none';

    return (
      <div
        data-test-sidebar
        className='sidebar'
        style={{ display: sidebarDisplay }}>

        <div className='sidebar-title'>
          <Image className="logo" src='data:image/svg+xml;base64,'/>
          <button
            data-test-sidebar-close-button
            className='close'
            onClick={toggleSidebar}>
            <Icon name='close' size='large' />
          </button>
        </div>

        <Menu pointing secondary vertical>
          <Menu.Item
            name='My tasks'
            as={NavLink}
            to='/tasks'
            activeClassName='active' />
          <Menu.Item
            name='Our Projects'
            as={NavLink}
            to='/our-projects'
            activeClassName='active' />
          <Menu.Item
            name='Project Directory'
            as={NavLink}
            to='projects'
            className='project-directory-item'
            activeClassName='active' />
          <Menu.Item
            name='Add Project'
            as={NavLink}
            to='add-projects'
            activeClassName='active' />
        </Menu>
      </div>
    );
  }
}

export default Sidebar;
